pragma solidity ^0.4.24;
pragma experimental ABIEncoderV2;

import "../PandoApp.sol";


contract BranchKit is PandoApp {

    event HandleRFC(uint256 rfcId);
    event ValuateRFC(uint256 rfcId, address sender, uint256 value);
    event SortRFC(uint256 rfcId, address sender, RFCSorting sorting);

    function handleRFC(uint256 rfcId) external;
    function valuateRFC(uint256 rfcId, uint256 value) external;
    function sortRFC(uint256 rfcId, RFCSorting sorting) external;
    function valuateAndSortRFC(uint256 rfcId, uint256 value, RFCSorting sorting) external;
}
