    bytes32 constant public SUBMIT_RFC_ROLE  = keccak256("SUBMIT_RFC_ROLE");
    bytes32 constant public SORT_RFC_ROLE    = keccak256("SORT_RFC_ROLE");


    event SubmitRFC (uint256 indexed id);
    event UpdateRFC (uint256 indexed id);
    event MergeRFC  (uint256 indexed id);
    event RejectRFC (uint256 indexed id);
    event CancelRFC (uint256 indexed id);

    event Test (address receiver, uint256 amount, string metadata);
    event Test2 (address origin);


    PandoHistory history;

    Pando.RFC[] public rfcs;

    function initialize(PandoHistory _history) onlyInit external {
