    function testRFL(Pando.RFL[] rfl) public {
        emit Test(rfl[0].receiver, rfl[0].amount, rfl[0].metadata);
    }

    function testCommit(Pando.Commit commit) public {
        emit Test2(commit.origin);
    }


    function _submitRFC(Pando.Commit _commit, Pando.RFL[] _rfls) isInitialized internal returns (uint256 id)  {
        id                    = rfcs.length++;
        Pando.RFC storage rfc = rfcs[id];

        rfc.state  = Pando.RFCState.Pending;
        rfc.commit = _commit;

        emit SubmitRFC(id);
    }

    function _sortRFC(uint256 _id, Pando.RFCSorting _sorting) internal {
        /* require(canSortRFC(rfcs[_id], _sorting)); */

        if(_sorting == Pando.RFCSorting.Merge) {
            rfcs[_id].state        = Pando.RFCState.Merged;
            rfcs[_id].commit.block = block.number;

            history.commit(rfcs[_id].commit.origin, rfcs[_id].commit.block, rfcs[_id].commit.tree, rfcs[_id].commit.message, rfcs[_id].commit.parents);

            /* for (uint i = 0; i < rfcs[_id].rfls.length) {
                issueRFL(rfcs[_id].rlfs[i]);
            } */

            /* specimen.issueReward(rfcs[_rfcId].commit.origin, rfcs[_rfcId].commit.value); */

            emit MergeRFC(_id);
        } else if (_sorting == Pando.RFCSorting.Reject) {
            rfcs[_id].state = Pando.RFCState.Rejected;
            emit RejectRFC(_id);
        }
    }

